We create the tables to be loaded with the data in the CSV files as follows.
CREATE TABLE agency ( agency_id text DEFAULT '', agency_name text DEFAULT NULL, agency_url text DEFAULT NULL, agency_timezone text DEFAULT NULL, agency_lang text DEFAULT NULL, agency_phone text DEFAULT NULL, CONSTRAINT agency_pkey PRIMARY KEY (agency_id) );
CREATE TABLE calendar ( service_id text, monday int NOT NULL, tuesday int NOT NULL, wednesday int NOT NULL, thursday int NOT NULL, friday int NOT NULL, saturday int NOT NULL, sunday int NOT NULL, start_date date NOT NULL, end_date date NOT NULL, CONSTRAINT calendar_pkey PRIMARY KEY (service_id) ); CREATE INDEX calendar_service_id ON calendar (service_id);
CREATE TABLE exception_types ( exception_type int PRIMARY KEY, description text );
CREATE TABLE calendar_dates ( service_id text, date date NOT NULL, exception_type int REFERENCES exception_types(exception_type) ); CREATE INDEX calendar_dates_dateidx ON calendar_dates (date);
CREATE TABLE route_types ( route_type int PRIMARY KEY, description text );
CREATE TABLE routes ( route_id text, route_short_name text DEFAULT '', route_long_name text DEFAULT '', route_desc text DEFAULT '', route_type int REFERENCES route_types(route_type), route_url text, route_color text, route_text_color text, CONSTRAINT routes_pkey PRIMARY KEY (route_id) );
CREATE TABLE shapes ( shape_id text NOT NULL, shape_pt_lat double precision NOT NULL, shape_pt_lon double precision NOT NULL, shape_pt_sequence int NOT NULL ); CREATE INDEX shapes_shape_key ON shapes (shape_id);
-- Create a table to store the shape geometries CREATE TABLE shape_geoms ( shape_id text NOT NULL, shape_geom geometry('LINESTRING', 4326), CONSTRAINT shape_geom_pkey PRIMARY KEY (shape_id) ); CREATE INDEX shape_geoms_key ON shapes (shape_id);
CREATE TABLE location_types ( location_type int PRIMARY KEY, description text );
